library(FinancialMath)

irr <- function(cfList) {
    return(IRR(cfList))
}

npv <- function(r, cfList) {
    sum_pv <- 0
    for (i in seq_along(cfList)) {
        sum_pv <- sum_pv + cfList[[i]] / ((1 + r) ^ i)
    }
    return(sum_pv)
}

