import::from("globals.R", INFLATION_FACTOR, .directory = here(h2a))
import::from("helpers.R", get, split, concat, .directory = here(h2a))
import::from("ref_tables.R", conversion_factor, get_aeo, .directory = here(h2a))
get_feedstock_price_for_year <- function(year, feedstock) {
    #'Get the price of a feedstock for a given year
    return(
        get(
            get(
                get_aeo(get(feedstock, "price_table")),
                get(feedstock, "name")
            ),
            year
        ) *
            get(feedstock, "usage") *
            conversion_factor(
                concat(
                  get(
                    split(
                      get(feedstock, "units"),
                      "/"
                  ),
                    0
                ),
                  "_to_$/GJ"
              )
            ) *
            INFLATION_FACTOR
    )
}
lookup_prices_for_feedstock <- function(analysis_range, feedstock) {
    #'Lookup prices for a given feedstock over a range of years
    return(
        lapply(analysis_range, function(year) get_feedstock_price_for_year(year, feedstock))
    )
}
get_feedstock_price_for_startup_year <- function(year, feedstock, startup_year) {
    #'Get the price of a feedstock for the startup year
    return(
        get(
            get(
                get_aeo(get(feedstock, "price_table")),
                get(feedstock, "name")
            ),
            startup_year
        ) *
            get(feedstock, "usage") *
