        )
    )
}
select_feedstock_prices <- function(feedstock, analysis_range, startup_year) {
    #'For a given feedstock, select whether to lookup prices or use a fixed price
    if (get(feedstock, "lookup_prices")) {
        return(lookup_prices_for_feedstock(analysis_range, feedstock))
    } else {
        return(fixed_prices_for_feedstock(analysis_range, feedstock, startup_year))
    }
}
